import { config } from "dotenv";
import { defineConfig, env } from "prisma/config";

// Load both .env and .env.local files
config({ path: ".env" });
config({ path: ".env.local" });

// æ ¹æ® DATABASE_URL çš„åè®®è‡ªåŠ¨é€‰æ‹©æ•°æ®åº“ç±»å‹
function getDatabaseProvider(url: string): string {
  if (!url) {
    throw new Error("DATABASE_URL is required");
  }

  if (url.startsWith("mysql://")) {
    return "mysql";
  } else if (url.startsWith("postgresql://") || url.startsWith("postgres://")) {
    return "postgresql";
  } else if (url.startsWith("sqlite://")) {
    return "sqlite";
  } else {
    throw new Error(`Unsupported database protocol in DATABASE_URL: ${url}. Supported: mysql://, postgresql://, postgres://, sqlite://`);
  }
}

const databaseUrl = env("DATABASE_URL");
const provider = getDatabaseProvider(databaseUrl);

// Log the detected provider for debugging
console.log(`ğŸ” Detected database provider: ${provider} for URL: ${databaseUrl.replace(/:[^:]*@/, ':***@')}`);

export default defineConfig({
  schema: "prisma/schema.prisma",
  migrations: {
    path: "prisma/migrations",
  },
  datasource: {
    url: databaseUrl,
  },
});
